        stream = BytesIO(buffer)

        # Read m_cNumApps (4 bytes, uint32)
        self.m_cNumApps = struct.unpack('<I', stream.read(4))[0]

        # Now read app IDs, each being a 4-byte little-endian integer
        for _ in range(self.m_cNumApps):
            app_id = struct.unpack('<I', stream.read(4))[0]
            request_all_sections = bool(struct.unpack('<I', stream.read(4))[0])
            self.app_ids.append((app_id, request_all_sections))

        # Check for any remaining bytes (if there's any unexpected data)
        remaining_bytes = stream.read()
        if remaining_bytes:
            print(f"Extra data found after app IDs: {remaining_bytes}")

        return self

